    const dish =dishes.find(el=>el.id===+id)
    return (
        <Row>
            <Col lg={7}>
                <img src={dish.image} alt="pic" className=""/>

            </Col>
            <Col lg={5}>

            </Col>
       dish details{id}
        </Row>
    );
};

export default DishDetails;
