import React from 'react';
import './menu.css'
import dishes from '../../dishes'
import {Row, Col,Card} from "react-bootstrap";

const Menu = () => {
    return (
        <div className="menu">
            <Row>
                {
                    dishes.map((dish)=>(
                        <Col lg={4}>
                            <Card className="mb-3">
                                <Card.Img variant="top" src={dish.image} />
                                <Card.Body>
                                    <Card.Title>{dish.name}</Card.Title>
                                    <Card.Text>
                                        {dish.description}
                                    </Card.Text>

                                </Card.Body>
                            </Card>
                        </Col>
                    ))
                }
            </Row>
        </div>

    );
};

export default Menu;
